import React from 'react';
import './Color.css'; 


const Color = (props) => {
	return (
		<div className='tc br3 ma2 pa1 shadow-4 bg-near-white bw2 color-list' style={{'background-color' : props.hexNumber}}>
			<p>{props.nameColor} {props.hexNumber}</p>
		</div> 
	);
}

export default Color;
