import React from 'react';
import './Color.css'; 


function Color(props){
	let temp = props.percentage * 100;
	temp = temp.toFixed(2);
	return (
		<div className='tc dib br3 ma2 pa2 shadow-4 bg-near-white bw2 color-list' style={{'color': props.hex}}>
			<div>{props.name}	</div>
			<div>{props.hex}	</div> 
			<div>{temp}%</div>
		</div> 
	);
}

export default Color;
