import React from 'react';
import './ColorDetector.css'; 

const ColorDetector = ({imageUrl, list}) => {
	return (
		<div className='center ma2'>
			<div className='ma2'>
				<img alt='' src={imageUrl}  className='w-100'/>
			</div>
			<div className='br3 pa2 ma2 shadow-4 bg-near-white' style={{color: list.hexNumber}}>
				<p>{list.nameColor} {list.hexNumber}</p>
			</div>
		</div>
	);
}

export default ColorDetector;
